import React,{useState} from "react";
import styled from "styled-components";
import { motion } from "framer-motion";

const Boxes = styled.div`
  background-color: gray;
  height: auto;
  width: auto;
  padding: 50px ;
  margin-top: 150px;
  margin-left: 90px;
  /* position: absolute ; */
  cursor: pointer;
  transition: 0.5s ease-in-out;
  border-radius: 10px;

  &:hover {
    background-color: white;
    box-shadow: 1px 1px 30px #343b7c;
  }
`;
const Title = styled.h3`
  font-size: 24px;
  text-align: center;
  color: black;
`;

const Desc = styled.p`
  width: 100%;
  padding: 15px;

`
const Box = ({ item }) => {
  const [ isOpen, setIsOpen ] = useState(false);

  return (
    <Boxes as={motion.div} layout transition={{layout:{duration:1, type:"spring"}}} onClick= {()=> setIsOpen(!isOpen)}>
      <Title  as={motion.h1} >{item.title}</Title>
      {isOpen && (
      <Desc>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Sequi ex
        dolorem laboriosam! Molestias!
      </Desc>
      )}
    </Boxes>
  );
};

export default Box;
